mapper.select:
SELECT * FROM
    (
        SELECT ROWNUM R, AL.*
        FROM (
                 SELECT
                    ID, USER_NAME, USER_APPROVAL, USER_DONG, USER_HO, USER_JOIN_DATE, USER_TEMP_NO
                 FROM TBL_USER
                 ORDER BY ID
             ) AL

        WHERE ROWNUM <= #{pagination.endRow}
    ) AL2
WHERE AL2.R >=#{pagination.startRow}
